//importamos el css
import './App.css';
// importamos los Estados para poder obtener los valores introducidos por el usuario
import { useState } from "react";
// importamos Axio, nos permite hacer sencillas las operaciones como cliente HTTP
import Axios from "axios";

function App() {
  // creamos las constantes para obtener los valores de los campos del furmulario
  const [nombreContacto, setNombreContacto] = useState("");
  const [direccionContacto, setDireccionContacto] = useState("");
  const [localidadContacto, setLocalidadContacto] = useState("");
  const [provinciaContacto, setProvinciaContacto] = useState("");
  const [telefonoContacto, setTelefonoContacto] = useState("");
  const [observacionesContacto, setObservacionesContacto] = useState("");
  const [correoContacto, setCorreoContacto] = useState("");
  const [modoCaptacion, setModoCaptacion] = useState("");
  // creamos una lista de clientes
  const [listaClientes, setClientes] = useState([]);

  const [mensaje, setMensaje] = useState("");

  // metodo para crear clientes
  const add = () =>
    // llamamos al metodo crear
    Axios.post("http://localhost:3001/create", {
      nombreContacto: nombreContacto,
      direccionContacto: direccionContacto,
      localidadContacto: localidadContacto,
      provinciaContacto: provinciaContacto,
      telefonoContacto: telefonoContacto,
      observacionesContacto: observacionesContacto,
      correoContacto: correoContacto,
      modoCaptacion: modoCaptacion,
    })
      .then((response) => {
        console.log("Datos enviados correctamente:", response);
        setMensaje("Datos enviados correctamente.");
      })
      .catch((error) => {
        console.error("Error al enviar los datos:", error);
        setMensaje("Error al enviar los datos.");
      });

  // metodo para obtener clientes
  const getClientes = () =>
    // llamamos a clientes
    Axios.get("http://localhost:3001/clientes")
      // cuando obtenga la respuesta lo guarde en una variable
      .then((response) => {
        // vienen todos los datos de clientes de la aplicacion
        setClientes(response.data);
        alert("Esta es la lista de clientes");
      })
      .catch((error) => {
        console.error("Error al mostrar los datos:", error);
      });

  return (
    // este seria el html visible en la pagina web
    <div className="App">

      <h1>Formulario de contacto</h1>

      <div className="datos">
        <label>Nombre completo del contacto: <input
          // se añaden los valores internos a cada uno de los campos, para poder asignar los valores introducidos a las variables creadas
          onChange={(event) => {
            // obtenemos (target) el valor (value) del campo (event) y lo cambiamos (set)
            setNombreContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Dirección del contacto: <input
          onChange={(event) => {
            setDireccionContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Localidad del contacto: <input
          onChange={(event) => {
            setLocalidadContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Provincia del contacto: <input
          onChange={(event) => {
            setProvinciaContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Teléfono de contacto: <input
          onChange={(event) => {
            setTelefonoContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Observaciones del contacto: <input
          onChange={(event) => {
            setObservacionesContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Correo del contacto: <input
          onChange={(event) => {
            setCorreoContacto(event.target.value);
          }}
          type="text"></input>
        </label>

        <label>Modo de captación: <select
          onChange={(event) => {
            setModoCaptacion(event.target.value);
          }}
          value={modoCaptacion}>
          <option value="" disabled select>Selecciona un modo de captación</option>
          <option value="Captador">Captador</option>
          <option value="Telemarketing">Telemarketing</option>
          <option value="Referido">Referido</option>
          <option value="Propia"> Captación propia</option>
        </select></label>

        <button onClick={add}>Registrar</button>
      </div>

      <div className="listaClientes">
        <button onClick={getClientes}>Listar clientes</button>{
          // map recorre todos los valores
          listaClientes.map((valor) => {
            //cada vez que encuentre un elemento mostrara la informaciomn
            return <div key={valor.id_cliente}>{valor.nombre}</div>;
          })
        }
      </div>
    </div>
  );
}

export default App;
